package adventofcode.day2;

import junit.framework.Assert;
import org.junit.Test;

/**
 * @author Mike Schmid
 */
public class Day2Puzzle2Test {

    @Test
    public void solve() throws Exception {
        final String testInput =
                "ULL\n" +
                        "RRDDD\n" +
                        "LURDL\n" +
                        "UUUUD";

        Assert.assertEquals("5DB3", Day2Puzzle2.solve(testInput));
    }

    @Test
    public void solveReal() throws Exception {
        final String testInput =
                "\n" +
                        "\n" +
                        "\n" +
                        "\n" +
                        "";

        Assert.assertEquals("CD8D4", Day2Puzzle2.solve(testInput));
    }

}