package adventofcode.day2;

import junit.framework.Assert;
import org.junit.Test;

/**
 * @author Mike Schmid
 */
public class Day2PuzzleTest {

    @Test
    public void solve() throws Exception {
        final String testInput =
                "ULL\n" +
                        "RRDDD\n" +
                        "LURDL\n" +
                        "UUUUD";

        Assert.assertEquals("1985", Day2Puzzle.solve(testInput));
    }

    @Test
    public void solveReal() throws Exception {
        final String testInput =
                "\n" +
                        "\n" +
                        "\n" +
                        "\n" +
                        "";

        Assert.assertEquals("98575", Day2Puzzle.solve(testInput));
    }

}