package adventofcode2016.day02;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

/**
 * @author Mike Schmid
 */
public class Day02PuzzleTest {

    @Test
    public void solve() throws Exception {
        final String testInput =
                "ULL\n" +
                        "RRDDD\n" +
                        "LURDL\n" +
                        "UUUUD";

        Assertions.assertEquals("1985", Day02Puzzle.solve(testInput));
    }

    @Test
    public void solveReal() throws Exception {
        final String testInput =
                "\n" +
                        "\n" +
                        "\n" +
                        "\n" +
                        "";

        Assertions.assertEquals("98575", Day02Puzzle.solve(testInput));
    }

}